import React from "react" 
import styled from 'styled-components';
import axios from "axios";
import { Link } from "react-router-dom";
import { LoginContext } from "../Contexts/LoginComponent";

export default function Entrada() {
    const {token} = React.useContext(LoginContext);
    const [valor,setValor] = React.useState("");
    const [descricao,setDescricao] = React.useState("");

    function submitData(event) {
        event.preventDefault();
        const config = {
            headers: { token: `Bearer ${token}` }
        };
        const obj = {
            amount: valor,
            description: descricao,
            type:"profit"
        };
        console.log(obj);

        const requisicao = axios.post('http://localhost:5000/entrada',obj);
        requisicao.then(function (sucesso){
            alert("nova despesa adicionada");
        });

        requisicao.catch(function (error){
            alert("erro ao adicionar despesa");
            console.log(error.response.data);
        });

        setEmail("");
        setUserName("");
        setPassword("");
        setPasswordConfirm("");

    }

    return(
        <Container>
            <h2>Nova Entrada</h2>
            <FormStyle>
                <form onSubmit={submitData}>
                    <input 
                    type="name" 
                    value = {valor}
                    onChange={(e) => setValor(e.target.value)}
                    required
                    placeholder="Valor"
                    />
                    <input 
                    type="name" 
                    value = {descricao}
                    onChange={(e) => setDescricao(e.target.value)}
                    required
                    placeholder="Descrição"
                    />                    
                     <button type="submit" >Salvar entrada</button>
                </form>
            </FormStyle>

        </Container>
    )
}


const Container = styled.div`
min-height: 100vh;
width:100%;
//background:#ffffff;
padding-top:68px;
display:flex;
flex-direction:column;
align-items:center;
`
const FormStyle = styled.div `
margin-top:52px;
    form{ 
        display:flex;
        flex-direction:column;
        align-items:center;
    }
    input{
        margin-bottom:10px;
        width: 303px;
        height: 45px;
        background: #FFFFFF;
        border: 1px solid #D5D5D5;
        border-radius: 5px;
        padding-left: 12px;
        &::placeholder{
            font-style: normal;
            font-weight: 400;
            font-size: 19.976px;
            line-height: 25px;
            color: #000000;
        }
    }
    button{
        width: 318px;
        height: 45px;
        background: #A328D6;
        border-radius: 4.63636px;
        margin-bottom:24px;
        color:#ffffff;
        font-family: 'Lexend Deca', sans-serif;
        font-style: normal;
        font-weight: 400;
        font-size: 20.976px;
        &:hover{
            cursor:pointer;
        }
    }
`
